import React, { useState, useRef } from "react";
import ReactToPrint from "react-to-print";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";
import ToolkitProvider, { Search } from "react-bootstrap-table2-toolkit";
import ReactBSAlert from "react-bootstrap-sweetalert";
import { dataTable } from "variables/general";
import Header from "components/Headers/Header.js";
//import 'bootstrap/dist/css/bootstrap.min.css';
import {
  Button,
  ButtonGroup,
  Container,
  Row,
  Col,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  FormGroup,
  Label,
  Input,
  UncontrolledTooltip
} from "reactstrap";

const AddClientModal = ({ isOpen, toggle }) => {
  return (
    <Modal isOpen={isOpen} toggle={toggle}>
      <ModalHeader toggle={toggle}>Ajouter un nouveau client</ModalHeader>
      <ModalBody>
        <FormGroup>
          <Label for="clientName">Nom</Label>
          <Input type="text" name="name" id="clientName" placeholder="Nom du client" />
        </FormGroup>
        <FormGroup>
          <Label for="clientEmail">Email</Label>
          <Input type="email" name="email" id="clientEmail" placeholder="Email du client" />
        </FormGroup>
        <FormGroup>
          <Label for="clientPhone">Téléphone</Label>
          <Input type="text" name="phone" id="clientPhone" placeholder="Téléphone du client" />
        </FormGroup>
        {/* Ajoutez d'autres champs selon les besoins */}
      </ModalBody>
      <ModalFooter>
        <Button color="primary" onClick={toggle}>Enregistrer</Button>{' '}
        <Button color="secondary" onClick={toggle}>Annuler</Button>
      </ModalFooter>
    </Modal>
  );
};

const Clients = () => {
  const [modal, setModal] = useState(false);
  const toggleModal = () => setModal(!modal);
  const componentRef = useRef(null);
  const [alert, setAlert] = useState(null);

  const pagination = paginationFactory({
    page: 1,
    alwaysShowAllBtns: true,
    showTotal: true,
    withFirstAndLast: false,
    sizePerPageRenderer: ({ options, currSizePerPage, onSizePerPageChange }) => (
      <div className="dataTables_length" id="datatable-basic_length">
        <label>
          Show{" "}
          {
            <select
              name="datatable-basic_length"
              aria-controls="datatable-basic"
              className="form-control form-control-sm"
              onChange={e => onSizePerPageChange(e.target.value)}
            >
              <option value="10">10</option>
              <option value="25">25</option>
              <option value="50">50</option>
              <option value="100">100</option>
            </select>
          }{" "}
          entries.
        </label>
      </div>
    )
  });

  const { SearchBar } = Search;

  const copyToClipboardAsTable = el => {
    var body = document.body, range, sel;
    if (document.createRange && window.getSelection) {
      range = document.createRange();
      sel = window.getSelection();
      sel.removeAllRanges();
      try {
        range.selectNodeContents(el);
        sel.addRange(range);
      } catch (e) {
        range.selectNode(el);
        sel.addRange(range);
      }
      document.execCommand("copy");
      setAlert(
        <ReactBSAlert
          success
          style={{ display: "block", marginTop: "-100px" }}
          title="Good job!"
          onConfirm={() => setAlert(null)}
          onCancel={() => setAlert(null)}
          confirmBtnBsStyle="info"
          btnSize=""
        >
          Copied to clipboard!
        </ReactBSAlert>
      );
    }
  };

  return (
    <>
      <Header />
      {alert}
      <ToolkitProvider
        data={dataTable}
        keyField="name"
        columns={[
          { dataField: "name", text: "Name", sort: true },
          { dataField: "position", text: "Position", sort: true },
          { dataField: "office", text: "Office", sort: true },
          { dataField: "Age", text: "age", sort: true },
          { dataField: "start_date", text: "Start date", sort: true },
          { dataField: "salary", text: "Salary", sort: true }
        ]}
        search
      >
        {props => (
          <div className="py-4">
            <Container fluid>
              <Row>
                <Col xs={12} sm={6}>
                  <ButtonGroup>
                    <Button
                      className="buttons-copy buttons-html5"
                      color="default"
                      size="sm"
                      id="copy-tooltip"
                      onClick={() =>
                        copyToClipboardAsTable(document.getElementById("react-bs-table"))
                      }
                    >
                      <span>Copy</span>
                    </Button>
                    <ReactToPrint
                      trigger={() => (
                        <Button
                          href="#"
                          color="default"
                          size="sm"
                          className="buttons-copy buttons-html5"
                          id="print-tooltip"
                        >
                          Print
                        </Button>
                      )}
                      content={() => componentRef.current}
                    />
                  </ButtonGroup>
                  <Button color="primary" onClick={toggleModal}>Ajouter Client</Button>
                  <UncontrolledTooltip placement="top" target="print-tooltip">
                    This will open a print page with the visible rows of the table.
                  </UncontrolledTooltip>
                  <UncontrolledTooltip placement="top" target="copy-tooltip">
                    This will copy to your clipboard the visible rows of the table.
                  </UncontrolledTooltip>
                </Col>
                <Col xs={12} sm={6}>
                  <div
                    id="datatable-basic_filter"
                    className="dataTables_filter px-4 pb-1 float-right"
                  >
                    <label>
                      Search:
                      <SearchBar
                        className="form-control-sm"
                        placeholder=""
                        {...props.searchProps}
                      />
                    </label>
                  </div>
                </Col>
              </Row>
            </Container>
            <BootstrapTable
              ref={componentRef}
              {...props.baseProps}
              bootstrap4={true}
              pagination={pagination}
              bordered={false}
              id="react-bs-table"
            />
          </div>
        )}
      </ToolkitProvider>
      <AddClientModal isOpen={modal} toggle={toggleModal} />
    </>
  );
};

export default Clients;
