import React, { useState, useRef } from "react";
import {
  Button,
  ButtonGroup,
  Card,
  Col,
  CardHeader,
  CardFooter,
  Container,
  FormGroup,
  Input,
  Label,
  Modal,
  ModalBody,
  ModalFooter,
  ModalHeader,
  Pagination,
  PaginationItem,
  PaginationLink,
  Row,
  UncontrolledTooltip
} from "reactstrap";
import ReactToPrint from "react-to-print";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory, { PaginationProvider, PaginationListStandalone } from "react-bootstrap-table2-paginator";
import ToolkitProvider, { Search } from "react-bootstrap-table2-toolkit";
import Header from "components/Headers/Header.js";
import { dataTable } from "variables/general";

const AddClientModal = ({ isOpen, toggle, onSave }) => {
  const [client, setClient] = useState({ name: "", email: "", phone: "" });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setClient({ ...client, [name]: value });
  };

  const handleSubmit = () => {
    onSave(client);
    toggle();
  };

  return (
    <Modal isOpen={isOpen} toggle={toggle}>
      <ModalHeader toggle={toggle}>Ajouter un nouveau client</ModalHeader>
      <ModalBody>
        <FormGroup>
          <Label for="clientName">Nom</Label>
          <Input type="text" name="name" id="clientName" placeholder="Nom du client" value={client.name} onChange={handleInputChange} />
        </FormGroup>
        <FormGroup>
          <Label for="clientEmail">Email</Label>
          <Input type="email" name="email" id="clientEmail" placeholder="Email du client" value={client.email} onChange={handleInputChange} />
        </FormGroup>
        <FormGroup>
          <Label for="clientPhone">Téléphone</Label>
          <Input type="text" name="phone" id="clientPhone" placeholder="Téléphone du client" value={client.phone} onChange={handleInputChange} />
        </FormGroup>
      </ModalBody>
      <ModalFooter>
        <Button color="primary" onClick={handleSubmit}>Enregistrer</Button>
        <Button color="secondary" onClick={toggle}>Annuler</Button>
      </ModalFooter>
    </Modal>
  );
};

const Tables = () => {
  const [modal, setModal] = useState(false);
  const toggleModal = () => setModal(!modal);
  const [clients, setClients] = useState(dataTable);
  const componentRef = useRef(null);

  const addNewClient = (client) => {
    setClients([...clients, { ...client, position: "New Position", office: "New Office", age: "N/A", start_date: "N/A", salary: "N/A" }]);
  };

  const copyToClipboardAsTable = () => {
    navigator.clipboard.writeText(JSON.stringify(clients))
      .then(() => {
        alert('Copied to clipboard successfully!');
      })
      .catch(err => {
        alert('Failed to copy: ', err);
      });
  };

  const paginationOptions = paginationFactory({
    page: 1,
    sizePerPage: 10,
    lastPageText: '>>',
    firstPageText: '<<',
    nextPageText: '>',
    prePageText: '<',
    showTotal: true,
    alwaysShowAllBtns: true,
    onPageChange: function (page, sizePerPage) {
      console.log('page', page);
      console.log('sizePerPage', sizePerPage);
    },
    onSizePerPageChange: function (sizePerPage, page) {
      console.log('page', page);
      console.log('sizePerPage', sizePerPage);
    }
  });

  return (
    <>
      <Header />
      <Container className="mt--7" fluid>
        <Row>
          <div className="col">
            <Card className="shadow">
              <CardHeader className="border-0">
                <h3 className="mb-0">Client tables</h3>
                <Button color="primary" onClick={toggleModal} style={{ float: "right" }}>Ajouter Client</Button>
              </CardHeader>
              <ToolkitProvider
                keyField="name"
                data={clients}
                columns={[
                  { dataField: "name", text: "Name", sort: true },
                  { dataField: "email", text: "Email", sort: true },
                  { dataField: "phone", text: "Phone", sort: true },
                  { dataField: "position", text: "Position", sort: true },
                  { dataField: "office", text: "Office", sort: true },
                  { dataField: "age", text: "Age", sort: true },
                  { dataField: "start_date", text: "Start Date", sort: true },
                  { dataField: "salary", text: "Salary", sort: true }
                ]}
                search
              >
                {props => (
                  <div className="p-4">
                    <Row>
                      <Col xs={12} sm={6}>
                        <ButtonGroup>
                          <Button
                            className="buttons-copy buttons-html5"
                            color="default"
                            size="sm"
                            id="copy-tooltip"
                            onClick={copyToClipboardAsTable}
                          >
                            Copy
                          </Button>
                          <ReactToPrint
                            trigger={() => (
                              <Button
                                color="info"
                                size="sm"
                                className="buttons-copy buttons-html5"
                                id="print-tooltip"
                              >
                                Print
                              </Button>
                            )}
                            content={() => componentRef.current}
                          />
                        </ButtonGroup>
                      </Col>
                      <Col xs={12} sm={6} className="text-right">
                        <Search.Bar {...props.searchProps} />
                      </Col>
                    </Row>
                    <BootstrapTable
                      ref={componentRef}
                      {...props.baseProps}
                      bootstrap4
                      pagination={pagination}
                      bordered={false}
                      id="react-bs-table"
                    />
                  </div>
                )}
              </ToolkitProvider>
              <CardFooter className="py-4">
                <PaginationProvider pagination={paginationOptions}>
                  {({ paginationProps, paginationTableProps }) => (
                    <PaginationListStandalone {...paginationProps} />
                  )}
                </PaginationProvider>
              </CardFooter>
            </Card>
          </div>
        </Row>
      </Container>
      <AddClientModal isOpen={modal} toggle={toggleModal} onSave={addNewClient} />
    </>
  );
};

export default Tables;
