import React, { useState, useRef } from "react";
import {
  Button,
  ButtonGroup,
  Card,
  Col,
  CardHeader,
  CardFooter,
  Container,
  FormGroup,
  Input,
  Label,
  Modal,
  ModalBody,
  ModalFooter,
  ModalHeader,
  Pagination,
  PaginationItem,
  PaginationLink,
  Row,
  UncontrolledTooltip
} from "reactstrap";
import ReactToPrint from "react-to-print";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";
import ToolkitProvider, { Search } from "react-bootstrap-table2-toolkit";
import ReactBSAlert from "react-bootstrap-sweetalert";
import Header from "components/Headers/Header.js";
import { dataTable } from "variables/general";

const AddClientModal = ({ isOpen, toggle, onSave }) => {
  const [client, setClient] = useState({ name: "", email: "", phone: "" });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setClient({ ...client, [name]: value });
  };

  const handleSubmit = () => {
    onSave(client);
    toggle();
  };

  return (
    <Modal isOpen={isOpen} toggle={toggle}>
      <ModalHeader toggle={toggle}>Ajouter un nouveau client</ModalHeader>
      <ModalBody>
        <FormGroup>
          <Label for="clientName">Nom</Label>
          <Input type="text" name="name" id="clientName" placeholder="Nom du client" value={client.name} onChange={handleInputChange} />
        </FormGroup>
        <FormGroup>
          <Label for="clientEmail">Email</Label>
          <Input type="email" name="email" id="clientEmail" placeholder="Email du client" value={client.email} onChange={handleInputChange} />
        </FormGroup>
        <FormGroup>
          <Label for="clientPhone">Téléphone</Label>
          <Input type="text" name="phone" id="clientPhone" placeholder="Téléphone du client" value={client.phone} onChange={handleInputChange} />
        </FormGroup>
      </ModalBody>
      <ModalFooter>
        <Button color="primary" onClick={handleSubmit}>Enregistrer</Button>
        <Button color="secondary" onClick={toggle}>Annuler</Button>
      </ModalFooter>
    </Modal>
  );
};

const Tables = () => {
  const [modal, setModal] = useState(false);
  const toggleModal = () => setModal(!modal);
  const [clients, setClients] = useState(dataTable);
  const componentRef = useRef(null);

  const addNewClient = (client) => {
    setClients([...clients, { ...client, position: "New Position", office: "New Office", age: "N/A", start_date: "N/A", salary: "N/A" }]);
  };

  const copyToClipboardAsTable = () => {
    navigator.clipboard.writeText(JSON.stringify(clients))
      .then(() => {
        alert('Copied to clipboard successfully!');
      })
      .catch(err => {
        alert('Failed to copy: ', err);
      });
  };

  const pagination = paginationFactory({
    page: 1,
    alwaysShowAllBtns: true,
    showTotal: true,
    withFirstAndLast: false,
    sizePerPageRenderer: ({ options, currSizePerPage, onSizePerPageChange }) => (
      <div className="dataTables_length" id="datatable-basic_length">
        <label>Afficher <select name="datatable-basic_length" aria-controls="datatable-basic" className="form-control form-control-sm" onChange={e => onSizePerPageChange(e.target.value)}>
          <option value="10">10</option>
          <option value="25">25</option>
          <option value="50">50</option>
          <option value="100">100</option></select> clients.</label>
      </div>
    )
  });

  const { SearchBar } = Search;

  return (
    <>
      <Header />
      <Container className="mt--7" fluid>
        <Row>
          <div className="col">
            <Card className="shadow">
              <CardHeader className="border-0">
                <h3 className="mb-0">Client tables</h3>
                <Button color="primary" onClick={toggleModal} style={{ float: "right" }}>Ajouter Client</Button>
              </CardHeader>
              <ToolkitProvider
                data={clients}
                keyField="name"
                columns={[
                  { dataField: "name", text: "Name", sort: true },
                  { dataField: "email", text: "Email", sort: true },
                  { dataField: "phone", text: "Phone", sort: true },
                  { dataField: "position", text: "Position", sort: true },
                  { dataField: "office", text: "Office", sort: true },
                  { dataField: "age", text: "Age", sort: true },
                  { dataField: "start_date", text: "Start Date", sort: true },
                  { dataField: "salary", text: "Salary", sort: true }
                ]}
                search
              >
                {props => (
                  <div className="p-4">
                  <Row>
                    <Col xs={12} sm={6}>
                      <ButtonGroup>
                      <Button
                        className="buttons-copy buttons-html5"
                        color="default"
                        size="sm"
                        id="copy-tooltip"
                        onClick={copyToClipboardAsTable}
                      >
                        <span>Copy</span>
                      </Button>
                        <ReactToPrint
                          trigger={() => (
                            <Button
                              color="info"
                              size="sm"
                              className="buttons-copy buttons-html5"
                              id="print-tooltip"
                            >
                              Print
                            </Button>
                          )}
                          content={() => componentRef.current}
                        />
                      </ButtonGroup>
                    </Col>
                    <Col xs={12} sm={6} className="text-right">
                      <SearchBar className="form-control-sm" placeholder="" {...props.searchProps} />
                    </Col>
                  </Row>
                    <BootstrapTable
                      ref={componentRef}
                      {...props.baseProps}
                      bootstrap4={true}
                      pagination={pagination}
                      bordered={false}
                      id="react-bs-table"
                    />
                  </div>
                )}
              </ToolkitProvider>
              <CardFooter className="py-4">
                {/* <nav aria-label="...">
                  <Pagination className="pagination justify-content-end mb-0" listClassName="justify-content-end mb-0">
                    <PaginationItem className="disabled">
                      <PaginationLink href="#pablo" onClick={e => e.preventDefault()} tabIndex="-1">
                        <i className="fas fa-angle-left" />
                        <span className="sr-only">Previous</span>
                      </PaginationLink>
                    </PaginationItem>
                    <PaginationItem className="active">
                      <PaginationLink href="#pablo" onClick={e => e.preventDefault()}>1</PaginationLink>
                    </PaginationItem>
                    <PaginationItem>
                      <PaginationLink href="#pablo" onClick={e => e.preventDefault()}>
                        2 <span className="sr-only">(current)</span>
                      </PaginationLink>
                    </PaginationItem>
                    <PaginationItem>
                      <PaginationLink href="#pablo" onClick={e => e.preventDefault()}>
                        3
                      </PaginationLink>
                    </PaginationItem>
                    <PaginationItem>
                      <PaginationLink href="#pablo" onClick={e => e.preventDefault()}>
                        <i className="fas fa-angle-right" />
                        <span className="sr-only">Next</span>
                      </PaginationLink>
                    </PaginationItem>
                  </Pagination>
                </nav> */}
              </CardFooter>
            </Card>
          </div>
        </Row>
      </Container>
      <AddClientModal isOpen={modal} toggle={toggleModal} onSave={addNewClient} />
    </>
  );
};

export default Tables;
